import axios from "axios";
import { useState } from "react";
import { Button, Col, Row } from "react-bootstrap";
import { Link } from "react-router-dom";

const Details = (props) => {
        let [mov,setMov]=useState([])
     const getPaginate = async (page) => {
       let res = await axios.get(
         `https://api.themoviedb.org/3/movie/popular?api_key=f1aca93e54807386df3f6972a5c33b50&language=ar&page=${page}`
       );

       setMovi(res.data.results);
       setPages(res.data.total_pages);
     };


    return (
      <>
        <div dir="rtl" className="card mb-3">
          <div className="row g-0">
            <div className="col-md-4">
              <img
                src="logo192.png"
                className="img-fluid rounded-start"
                alt="..."
              />
            </div>
            <div className="col-md-8">
              <div className="card-body">
                <p>اسم الفلم : </p>
                <p>تاريخ الاصدار :</p>

                <p>التقيم : </p>
              </div>
            </div>
          </div>
        </div>
        <Row dir="rtl" className="border">
          <h2>قصة الفلم </h2>
          <p></p>
        </Row>
        <Row dir="rtl" className="d-flex justify-content-center my-3">
          <Col className="">
            <Link to="/">
              <Button>الرجوع للصفحة الرئيسية</Button>
            </Link>
          </Col>
          <Col>
            <Button className="">مشاهدة الفلم</Button>
          </Col>
        </Row>
      </>
    );
    
}
export default Details
